import router from '../../router/index'; import rules from'../../utils
/validation.rule';
<template>
  <div class="login-page">
    <div class="login-form">
      <ul class="tab">
        <li
          :class="{ active: tab.selected }"
          v-for="tab in tabs"
          :key="tab.id"
          @click="toggleTab(tab)"
        >
          {{ tab.text }}
        </li>
      </ul>
      <el-form
        :model="ruleForm"
        status-icon
        :rules="rules"
        class="form-body"
        size="medium"
      >
        <el-form-item prop="email" class="form-item">
          <label>邮箱</label>
          <el-input
            type="text"
            v-model="ruleForm.email"
            autocomplete="off"
          ></el-input>
        </el-form-item>
        <el-form-item prop="password" class="form-item">
          <label>密码</label>
          <el-input
            type="password"
            v-model="ruleForm.password"
            autocomplete="off"
            minlength="6"
            maxlength="20"
          ></el-input>
        </el-form-item>
        <el-form-item prop="authCode" class="form-item">
          <label>验证码</label>
          <el-row :gutter="10">
            <el-col :span="16">
              <el-input
                vm-model.number="ruleForm.authCode"
                autocomplete="off"
                maxlength="6"
                minlength="6"
              ></el-input
            ></el-col>
            <el-col :span="8">
              <el-button type="success" class="btn-block">获取验证码</el-button>
            </el-col>
          </el-row>
        </el-form-item>
        <el-form-item class="form-actions">
          <el-button type="danger" @click="submitForm('ruleForm')"
            >提交</el-button
          >
        </el-form-item>
      </el-form>
    </div>
  </div>
</template>
<script>
import rules from "../../lib/validation.rule";
export default {
  name: "login",
  data() {
    const rulePwd = (rule, value, callback) => {
      console.log(value.trimScript());
      const reg = /^(?!\D+$)(?![^a-zA-Z]+$)\S{6,20}$/g;
      if (!value) callback();
      else if (!reg.test(value)) {
        callback(new Error("密码为6-20位的数字和字母"));
      } else callback();
    };
    const ruleCode = (rule, value, callback) => {
      const reg = /^[0-9a-zA-Z]{6}$/g;
      if (!value || !reg.test(value)) callback(new Error("请输入验证码"));
      else callback();
    };
    return {
      ruleForm: {
        email: "",
        password: "",
        authCode: ""
      },
      rules: {
        email: [
          { validator: rules.email, trigger: "blur" },
          { required: true, message: "请输入邮箱", trigger: "blur" }
        ],
        password: [
          { validator: rulePwd, trigger: "blur" },
          { required: true, message: "请输入密码", trigger: "blur" }
        ],
        authCode: [{ validator: ruleCode, trigger: "blur" }]
      },
      tabs: [
        { selected: true, text: "登陆" },
        { selected: false, text: "注册" }
      ]
    };
  },
  methods: {
    toggleTab(tab) {
      this.tabs.forEach(item => (item.selected = false));
      tab.selected = true;
    }
    // submitForm(formName) {
    //   this.$refs[formName].validate(valid => {
    //     if (valid) alert("submit");
    //     else {
    //       console.log("error submit");
    //       return false;
    //     }
    //   });
    // }
  }
};
</script>
<style lang="scss" scoped>
$backgroud: #344a5f;
.login-page {
  background-color: $backgroud;
  height: 100vh;
  .login-form {
    width: 330px;
    margin: auto;
    .tab {
      text-align: center;
      li {
        display: inline-block;
        width: 88px;
        height: 36px;
        line-height: 36px;
        font-size: 14px;
        border-radius: 2px;
        color: #fff;
        cursor: pointer;
        &.active {
          background-color: darken($color: $backgroud, $amount: 10%);
        }
        &:hover:not(.active) {
          background-color: darken($color: $backgroud, $amount: 8%);
        }
      }
    }
    .form-body {
      margin-top: 29px;
      .btn-block {
        display: block;
        width: 100%;
      }
      .form-item {
        margin-bottom: 13px;
        label {
          display: block;
          font-size: 14px;
          color: #fff;
          margin-bottom: 3px;
        }
      }
      .form-actions {
        margin-top: 19px;
        button {
          @extend .btn-block;
        }
      }
    }
  }
}
</style>
