import router from '../../router/index';
<template>
  <div class="login-page">
    <div class="login-form">
      <ul class="tab">
        <li
          :class="{ active: tab.selected }"
          v-for="tab in tabs"
          :key="tab.id"
          @click="toggleTab(tab)"
        >
          {{ tab.text }}
        </li>
      </ul>
      <el-form
        :model="ruleForm"
        status-icon
        :rules="rules"
        label-width="100px"
        class="demo-ruleForm"
      >
        <el-form-item label="密码" prop="pass">
          <el-input
            type="password"
            vm-model="ruleForm.pass"
            autocomplete="off"
          ></el-input>
        </el-form-item>
      </el-form>
    </div>
  </div>
</template>
<script>
export default {
  name: "login",
  data() {
    const checkAge = (rule, value, callback) => {
      if (!value) return callback(new Error("年龄不能为空"));
      setTimeout(() => {
        if (!Number.isInteger(value)) {
          callback(new Error("请输入数字值！"));
        } else {
          if (value < 18) callback(new Error("必须年满18岁"));
          else callback();
        }
      }, 1000);
    };
    const validatePass = (rule, value, callback) => {
      if (value === "") callback(new Error("请输入密码"));
      else {
        if (this.ruleForm.checkPass !== "") {
          this.$refs.rule.validateField("checkPass");
        }
        callback();
      }
    };
    const validatePass2 = (rule, value, callback) => {
      if (value === "") callback(new Error("请再次输入密码"));
      else if (value !== this.ruleForm.pass) {
        callback(new Error("两次输入密码不一致"));
      } else callback();
    };
    return {
      ruleForm: {
        pass: "",
        checkPass: "".checkPass,
        age: ""
      },
      rules: {
        pass: [{ validator: validatePass, trigger: "blur" }],
        checkPass: [{ validator: validatePass2, trigger: "blur" }],
        age: [{ validator: checkAge, trigger: "blur" }]
      },
      tabs: [
        { selected: true, text: "登陆" },
        { selected: false, text: "注册" }
      ]
    };
  },
  methods: {
    toggleTab(tab) {
      this.tabs.forEach(item => (item.selected = false));
      tab.selected = true;
    },
    submitForm(formName) {
      this.$refs[formName].validate(valid => {
        if (valid) alert("submit");
        else {
          console.log("error submit");
          return false;
        }
      });
    }
  }
};
</script>
<style lang="scss" scoped>
$backgroud: #344a5f;
.login-page {
  background-color: $backgroud;
  height: 100vh;
  .login-form {
    width: 330px;
    margin: auto;
    .tab {
      text-align: center;
      li {
        display: inline-block;
        width: 88px;
        height: 36px;
        line-height: 36px;
        font-size: 14px;
        border-radius: 2px;
        color: #fff;
        cursor: pointer;
        &.active {
          background-color: darken($color: $backgroud, $amount: 10%);
        }
        &:hover:not(.active) {
          background-color: darken($color: $backgroud, $amount: 8%);
        }
      }
    }
  }
}
</style>
